import * as React from "react";
import { Button, StyleSheet, Text, View } from "react-native";
import { RadioButton, TextInput } from "react-native-paper";
const HomeTab = (props) => {
  const [checked, setChecked] = React.useState("first");
  return (
    <View style={styles.container}>
      <Text style={styles.header}>Upload Document</Text>

      <Text style={styles.text}>1. Document Type </Text>
      <View style={{ flexDirection: "row", fontSize: 20 }}>
        <RadioButton
          title="Color"
          value="first"
          status={checked === "first" ? "checked" : "unchecked"}
          onPress={() => setChecked("first")}
        />
        <Text style={{ padding: 3, fontSize: 20, marginRight: "10%" }}>
          {" "}
          Color{" "}
        </Text>
        {/* </View>

<View style={{ flexDirection:'row', fontSize: 20 }}> */}
        <RadioButton
          title="Black and White"
          value="second"
          status={checked === "second" ? "checked" : "unchecked"}
          //onPress={() => setChecked("second")}
        />
        <Text style={{ padding: 3, fontSize: 20, width: "100%" }}>
          {" "}
          Black/White{" "}
        </Text>
      </View>
      {/* </View>
      <Text style={styles.text}>2. Copies</Text>

      {/* <TextInput
        label="copies"
        //value={text}
        //onChangeText={(text) => setText(text)}
      /> */}

      <Text style={styles.text}>3. Urgent Service </Text>
      <Text>
        <RadioButton
          value="first"
          status={checked === "first" ? "checked" : "unchecked"}
          onPress={() => setChecked("first")}
        />
        Yes
        <RadioButton
          value="second"
          status={checked === "second" ? "checked" : "unchecked"}
          onPress={() => setChecked("second")}
        />
        No
      </Text>
      <Text style={styles.text}>4. Upload File</Text>
      <Text>
        <Button title="Submit" />
      </Text>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    paddingTop: "3%",
    flex: 1,
    alignItems: "center",
    //justifyContent: "center",
    backgroundColor: "white",
  },
  header: {
    fontSize: 26,
    fontWeight: "bold",
  },
  text: {
    marginBottom: "10%",
    marginTop: "10%",
    fontSize: 20,
  },
});
export default HomeTab;
